
from BioExt.scorematrix import HIV_BETWEEN_F
from BioExt.aligner import Aligner

hbf = HIV_BETWEEN_F.load()
aln = Aligner(hbf, do_codon=True)

ref = ''
query = ''

print(set(ref))
print(set(query))

for i in range(100):
    s, r, q = aln(ref, query, extend_insertion=1, extend_deletion=1)

print(s)
print(r)
print(q)
